E_D("DROP TABLE IF EXISTS `xia_article`;");
E_C("CREATE TABLE `xia_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cid` int(11) DEFAULT NULL,
  `title` varchar(200) DEFAULT NULL,
  `img` varchar(255) DEFAULT NULL,
  `source` varchar(200) DEFAULT NULL,
  `content` text,
  `hits` int(11) DEFAULT '0',
  `sort` int(11) DEFAULT '0',
  `keyword` varchar(255) DEFAULT NULL,
  `desc` text,
  `addtime` int(10) NOT NULL DEFAULT '0',
  `del` tinyint(1) NOT NULL DEFAULT '0',
  `tj` int(1) DEFAULT '0',
  `xia` varchar(233) DEFAULT NULL,
  `addtime1` varchar(222) DEFAULT NULL,
  `model` varchar(100) DEFAULT NULL,
  `shebei` varchar(100) DEFAULT NULL,
  `didian` varchar(200) DEFAULT NULL,
  `shijian` varchar(100) DEFAULT NULL,
  `jian_url` varchar(266) DEFAULT NULL,
  `school` varchar(200) DEFAULT NULL,
  `wei` varchar(100) DEFAULT NULL,
  `gid` int(10) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `cid` (`cid`),
  KEY `title` (`title`),
  KEY `sort` (`sort`),
  KEY `addtime` (`addtime`),
  KEY `del` (`del`)
) ENGINE=MyISAM AUTO_INCREMENT=2189 DEFAULT CHARSET=utf8");
E_D("replace into `xia_article` values('2187','721','地铁空气净化器，解决空气质量问题','http://www.365cm.net/d/file/201908/a5c71c1304f6f48a9e012ce7b885b725.png','','<p style=\"margin-top:10px;margin-bottom:0px;padding:0px 0px 24px;white-space:normal;box-sizing:border-box;line-height:36px;background-color:#FFFFFF;font-family:\" color:#333333;font-size:18px;\"=\"\">\r\n	地铁作为上班族和上学党的主要交通工具之一，尤其是在北上广深等一些大城市，地铁几乎已经是最主要的交通工具了。地铁在给我们的出行带来方便的同时，也存在着一些问题，除了上下班高峰痛苦无比的人挤人，还有个一直比较受到关注的，那就是地铁里的空气质量问题，那就一定要说到地铁净化器了。\r\n	</p>\r\n<p style=\"margin-top:10px;margin-bottom:0px;padding:0px 0px 24px;white-space:normal;box-sizing:border-box;line-height:36px;background-color:#FFFFFF;font-family:\" color:#333333;font-size:18px;\"=\"\">\r\n	　　经过研究发现，地铁内的空气污染物主要来源于三个方面，其一是地铁内的装饰材料和保温材料会直接释放出包括挥发性有机化合物等的多种化学污染物;其二是由于地铁是相对密闭的空间，高度密集的人群会释放出大量异味和二氧化碳，并产生各种细菌等生物污染物;第三点也是比较严重的一点，就是由于铁轨与车轮长期处于高温摩擦的状态，造成空气中铁微粒等超细金属粉尘会长时间漂浮在地铁的空气中，并随之进入人体，对人体会产生很大的危害。而不管地铁的空气怎么差，对于那些上班族来说，挤地铁是没有办法的事，再怎么脏都要挤进去。\r\n</p>\r\n<p style=\"margin-top:10px;margin-bottom:0px;padding:0px 0px 24px;white-space:normal;box-sizing:border-box;line-height:36px;background-color:#FFFFFF;font-family:\" color:#333333;font-size:18px;\"=\"\">\r\n	　　不过地铁空气质量差也不是没有办法解决的，除了出门佩戴口罩，多洗手这些小习惯可以有效缓解问题外，长沙地铁3/4号线给出了答案，那就是首先在人流聚集的各个地铁净化器。<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style=\"font-family:&quot;color:#333333;font-size:18px;\">目前为各城市地铁打造“绿色地铁”空气净化服务，从地铁站入口刚进入，第一感受就是空气的清新，为地铁站提供源源不断的优质空气，给市民的地铁出行带来了最洁净、最健康的纯净之旅是地铁净化器的初衷和使命。</span>\r\n	</p>\r\n<p style=\"margin-top:10px;margin-bottom:0px;padding:0px 0px 24px;white-space:normal;box-sizing:border-box;line-height:36px;background-color:#FFFFFF;font-family:\" color:#333333;font-size:18px;\"=\"\">\r\n	<p style=\"margin-top:10px;margin-bottom:0px;padding:0px 0px 24px;white-space:normal;box-sizing:border-box;line-height:36px;background-color:#FFFFFF;font-family:&quot;color:#333333;font-size:18px;\">\r\n		　　地铁公司的这一举措，也将彻底改善大众对“地铁站空气质量差”的印象。希望广大市民也可以自主爱护公共财产，保护好我们的地铁净化器。\r\n	</p>\r\n</p>','897','0','深圳市伟泰环保工程有限公司','','1569408163','0','1','',NULL,NULL,NULL,NULL,NULL,'',NULL,NULL,NULL);");
E_D("replace into `xia_article` values('2188','721','使用环保公共厕所，为保护环境贡献一份力量','upload/2019/0925/18475745939.jpeg','','<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">现在我们国家无论大小城市都在注重环保，从各个方面，都在保护环境。保护环境要从我们身边做起，这样才是真正的保护环境。环保厕所是其中很重要的一部分。</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">【1】公共厕所，与我们人类生存和生活有着密切的联系。公共厕所为我们城市美化环境作出了重要贡献，但是传统的公共厕所，弊端越来越多。公共厕所内部尽管厕所空间大，但是，管理起来也非常不方便，由于人流量大，打扫起来非常不方便。如果没有人管理公共厕所，几天就会成为脏乱差的场所，臭味甚至在几百米外都能闻到。</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">【2】而现在的环保厕所，完全避开了传统厕所的弊端。占地小，节约用水，安全环保，外形美观，环保厕所的优点实在太多了。它帮助城市的环境美化作出了重要的贡献，对环境保护有着积极的意义。</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">【3】环保厕所有效的保护着一些所特有的自然环境体系。</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">对于建筑中所进行的开发与破坏进行有效的防治，现在很多的开发商都会由于地基价值，从而引起了在更多地区进行开发建造别墅的，竟然没有很好的保护环境，实现较好的发展措施环保厕所具备着较多的实际应用特征，有效促使我们在正确进行环境美化的同时，很好的帮助保护自然环境的建设。</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">【4】走进环保公厕，你会发现这是一个处处用心建造的公厕，公厕内部空间设计合理，室内装修大气美观，公厕里不仅闻不到一点臭味，空气中含有一股清香，公厕除了设置男女厕所外，还贴心的设计了专用母婴室和休息室，方便游客。工程里的残疾人专用合金也很注重细节，配备专用扶手，感应式冲水器，空调等电气设备配备齐全。</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">环保厕所具备着较好的，对于城市的便利性较为完善的优化方式，更为体现出现在以人为本的价值观念普及，更加保证人们的生活水平逐渐提高，所以说要想保护环境，就要全方面的，从我们身边每个小时做起，才会有更好的效果，现在城市离不开环保厕所，我们的生活也离不开环保厕所。</span> \r\n</p>','973','0','深圳市伟泰环保工程有限公司','','1569408387','0','1','',NULL,NULL,NULL,NULL,NULL,'',NULL,NULL,NULL);");
E_D("replace into `xia_article` values('2185','721','全方位室内空气净化，环保让呼吸更自由”主题大会顺利召开','','','<p style=\"margin-top:0px;margin-bottom:30px;padding:0px;border:0px;font-family:&quot;list-style:none;color:#4D4F53;font-size:18px;letter-spacing:1px;white-space:normal;text-indent:2em;text-align:justify;\">\r\n	9月10日，以“全方位室内空气净化，环保让呼吸更自由”为主题的大会顺利召开。会议中集中了环保业界的企业、集团，可谓大咖云云。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:30px;padding:0px;border:0px;font-family:&quot;list-style:none;color:#4D4F53;font-size:18px;letter-spacing:1px;white-space:normal;text-indent:2em;text-align:justify;\">\r\n	会议表达了近期甲醛越发肆虐，新装修环境下的空气质量令人担忧。如果空气中甲醛浓度过高，会明显刺激黏膜、损害呼吸系统甚至致癌;哪怕浓度不高，长期接触也可能导致慢性呼吸道疾病。特别是对身体较弱的儿童和老人，危害更大。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:30px;padding:0px;border:0px;font-family:&quot;list-style:none;color:#4D4F53;font-size:18px;letter-spacing:1px;white-space:normal;text-indent:2em;text-align:justify;\">\r\n	深圳市中拓智森环保科技有限公司董事长刘忠坤指出，室内空气污染种类有很多，在行业通常单独处理其中某一种室内空气污染，从室内空气环境健康角度上来说完全是不够的，这是治标不治本的做法。面对这个严峻的问题，他介绍了室内空气污染分为三大类污染，分别是化学性污染、物理性污染、生物性污染。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:30px;padding:0px;border:0px;font-family:&quot;list-style:none;color:#4D4F53;font-size:18px;letter-spacing:1px;white-space:normal;text-indent:2em;text-align:justify;\">\r\n	据悉，本次大会中可谓行业矛头的深圳市中拓智森环保科技有限公司参与过很多社会活动。刘忠坤心系家乡，关注慈善事业，为了更好的回馈社会帮助家乡发展，免费为学校幼儿园、企业单位提供室内空气污染治理服务。该公司生产的空气触媒产品让更多的孩子能呼吸到新鲜无污染的空气，还孩子一个健康的童年。同时也推动了环境环保方面的社会进展。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:30px;padding:0px;border:0px;font-family:&quot;list-style:none;color:#4D4F53;font-size:18px;letter-spacing:1px;white-space:normal;text-indent:2em;text-align:justify;\">\r\n	刘忠坤非常重视科研的投入、科技人才的培养。在公司设立大学生实习基地，与华南理工大学化化工学院展开合作，为学校应届毕业生提供实习岗位，为企业补充人才、留用选择优秀的实习生。可谓业界中知名的善于用才、惜才之人。\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:30px;padding:0px;border:0px;font-family:&quot;list-style:none;color:#4D4F53;font-size:18px;letter-spacing:1px;white-space:normal;text-indent:2em;text-align:justify;\">\r\n	希望本次会议能为各地企业机构带来帮助，也希望更多的企业参与到“全方位室内空气净化，环保让呼吸更自由”活动中来，共同推进行业健康发展。\r\n</p>','387','0','深圳市伟泰环保工程有限公司','','1569407811','0','1','',NULL,NULL,NULL,NULL,NULL,'',NULL,NULL,NULL);");
E_D("replace into `xia_article` values('2186','721','房子装修好多长时间才能入住？家居环保从5方面入手，清新空气','upload/2019/0925/18421719212.jpeg','','<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">房子装修好多长时间才能入住？家居环保从5方面入手，清新空气</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">装修好的房子一般不会马上入住的，必须要空一段时间。就算是现在很多的装修家具都是环保的，但是也不能保证百分之百完全的环保，只能说是达到了环保标准。何况在装修的过程中也会有灰尘之类的，那么装修之后多久住进去比较合适呢？房子装修好多长时间才能入住？家居环保从5方面入手，清新空气。</span>\r\n</p>\r\n<p style=\"margin-top:26px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">1：至少要通风三个月才可以住进去</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">在装修的时候会安装很多的家具，除了家具之外还会使用到胶水，这些黏合剂也是属于化学物质，所以会造成空气的污染。尤其是新房子刚刚装修之后，里面还会有一股味道的，因此才装修好的房子肯定是不能住进去的，还需要晾几个月的时间，一般至少是三个月的时间，这期间一定要将门窗打开，保证室内的空气流通。</span>\r\n</p>\r\n<p style=\"margin-top:26px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">2：室内一定要保证是通风透气的环境</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">在装修之后肯定是不能将门窗关起来的，必须要在天气好的情况下将门窗都打开。除了对环保有帮助，关键也是保护好家具，尤其是在梅雨季节的时候，更加要重视室内的通风，要不然一直阴雨天会影响到家具的质量，可能会导致发霉的。</span>\r\n</p>\r\n<p style=\"margin-top:26px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">3：敞开所有的柜门进行透气</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">室内通风换气并不仅仅指的是门窗，家具的柜子门也都是要打开的，比如衣柜，橱柜等等，将所有的柜子门都打开，要不然柜子里面也是会有味道的，打开之后可以将里面的味道散去。其次如果一直不打开的话，里面也可能会有发霉的情况，甚至板材还可能会有褪色的情况出现。</span>\r\n</p>\r\n<p style=\"margin-top:26px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">4：使用酚醛油漆的话需要晾置更久的时间</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">在装修的时候可能会使用到酚醛油漆，这种油漆里面的化学物质也是很多的，</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">所以挥发的时间也是比较久的，那么在装修之后要等待入住的时间也会更久。还有就是乳胶漆的使用，很多卧室的墙面都是刷的乳胶漆，一定要等待乳胶漆全部都干了才能考虑入住，当然墙面的防水工程是不能少的。</span>\r\n</p>\r\n<p style=\"margin-top:26px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">5：使用绿植帮助环保</span> \r\n</p>\r\n<p style=\"margin-top:22px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">在新房子装修好之后需要摆放好绿植，这个绿植对于环保是很有帮助的，而且要根据不同的功能区域摆放不同的绿植。例如玄关的位置可以摆放发财树，卧室里面适合摆放绿萝等等，绿植还具有一定的观赏价值。</span>\r\n</p>\r\n<p style=\"margin-top:26px;margin-bottom:0px;padding:0px;font-size:16px;line-height:24px;color:#333333;text-align:justify;font-family:arial;white-space:normal;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">总之新房装修好了之后一定要重视环保，不要马上就入住，或者是找专业的机构进行甲醛检测。</span> \r\n</p>','878','0','深圳市伟泰环保工程有限公司','','1569408068','0','1','',NULL,NULL,NULL,NULL,NULL,'',NULL,NULL,NULL);");
